import React, { useEffect, useReducer, useCallback, useState } from "react";
import "./css/HomePage.css";
import { useNavigation } from "../Router/Router";
import io from 'socket.io-client';
const HomePage = () => {
  const { onClickStart, onClickOwner, onClickPay, onClickDone, onClickMain } =
    useNavigation();
    const item_list = [];
    useEffect(() => {
      const socket = io.connect('http://127.0.0.1:5000');
        try {
          console.log("실행중");
          const message ={"message" : "전송완료"}
          socket.emit('call_owner', message)
          alert("전송완료")
        } 
        catch(error){
          alert("전송실패")
        }
      // 클린업 함수
      return () => {
        socket.disconnect(); // 컴포넌트가 언마운트될 때 소켓 연결 해제
      };
    }, []);
  return (
    <>
      <button className="ownerCall" onClick={onClickOwner}>
        직원 호출버튼
      </button>
      <div className="selfCounter">무인 계산대</div>

      <button className="start" onClick={onClickStart}>
        시작하기
      </button>
      <div className="back"></div>
      <div className="img"></div>
    </>
  );
};
export default HomePage;
