import React, { useEffect, useReducer, useCallback, useState } from "react";

import { useNavigation } from "../Router/Router";
import "./css/PayPage.css"
import Axios from 'axios';
const PayPage = () => {
  useEffect(() => {
    // fetchData 함수 정의
    const fetchData = async () => {
      try {
        console.log("실행중");
        const response = await Axios.get('get_products');


        for (let i = 0; i < response.data.item.length; i++) {
          if (item_list.length === 0) {
            item_list.push([response.data.item[i].amount, response.data.item[i].name, response.data.item[i].price, response.data.item[i].image]);
            continue;
          }

          let breakpoint = false;

          for (let j = 0; j < item_list.length; j++) {
            if (item_list[j][1] === response.data.item[i].name) {
              item_list[j][0]++;
              breakpoint = true;
              break;
            }
          }

          if (!breakpoint) {
            item_list.push([response.data.item[i].amount, response.data.item[i].name, response.data.item[i].price, response.data.item[i].image]);
          }
        }

        // 업데이트된 상품으로 상태 업데이트
        const updatedProducts = item_list.map(([amount, name, price, image]) => ({
          amount,
          name,
          price,
          image,
        }));
        dispatch({ type: "SET_PRODUCTS", data: updatedProducts });

      } catch (error) {
        console.error('데이터를 가져오는 중 오류 발생:', error);
      }
    };

    // fetchData 함수 초기 호출
    fetchData();

  }, []);
  
  const { onClickStart, onClickOwner, onClickPay, onClickDone, onClickMain } =
    useNavigation();
  return (
    <div className="wrapper1">
          <header>
            <div className="Header1">
              <div className="goods1">1. 상품확인</div>
              <div className="pay1">2. 결제 방법</div>
              <div className="done1">3. 결제 완료</div>
            </div>
          </header>
      <div className="wrapper">
        <div className="txt">결제 수단을 선택해주세요</div>
        <div className="selectCash">
          <button className="cash" onClick={onClickDone}>현금</button>
          <button className="card"  onClick={onClickDone}>카드</button>
        </div>
      </div>
    </div>
  );
};
export default PayPage;
