import React, { useEffect } from "react";
import { useNavigation } from "../Router/Router";
import "./css/PayPage.css";
import Axios from "axios";

const PayPage = () => {
  const { onClickStart, onClickOwner, onClickPay, onClickDone, onClickMain } =
    useNavigation();
    const Payment = () => {
      useEffect(() => {
        const jquery = document.createElement("script");
        jquery.src = "http://code.jquery.com/jquery-1.12.4.min.js";
        const iamport = document.createElement("script");
        iamport.src = "http://cdn.iamport.kr/js/iamport.payment-1.1.7.js";
        document.head.appendChild(jquery);
        document.head.appendChild(iamport);
        return () => {
          document.head.removeChild(jquery);
          document.head.removeChild(iamport);
        };
      }, []);
    
      const requestPay = () => {
        const { IMP } = window;
        IMP.init('가맹점식별코드');
    
        IMP.request_pay({
          pg: '{PG사코드}.{PG상점ID}',
          pay_method: 'card',
          merchant_uid: new Date().getTime(),
          name: '테스트 상품',
          amount: 1004,
          buyer_email: 'test@naver.com',
          buyer_name: '코드쿡',
          buyer_tel: '010-1234-5678',
          buyer_addr: '서울특별시',
          buyer_postcode: '123-456',
        }, async (rsp) => {
          try {
            const { data } = await Axios.post('http://localhost:8080/verifyIamport/' + rsp.imp_uid);
            if (rsp.paid_amount === data.response.amount) {
              alert('결제 성공');
            } else {
              alert('결제 실패');
            }
          } catch (error) {
            console.error('Error while verifying payment:', error);
            alert('결제 실패');
          }
        });
      };
    }

  return (
    <div className="wrapper1">
      <header>
        <div className="Header1">
          <div className="goods1">1. 상품확인</div>
          <div className="pay1">2. 결제 방법</div>
          <div className="done1">3. 결제 완료</div>
        </div>
      </header>
      <div className="wrapper">
        <div className="txt">결제 수단을 선택해주세요</div>
        <div className="selectCash">
          <button className="cash"  onClick={onClickPayment}>
            카카오페이
          </button>
          <button className="card" onClick={onClickDone}>
            현금
          </button>
        </div>
      </div>
    </div>
  );
};

export default PayPage;
