import React, { useEffect } from "react";
import { useNavigation } from "../Router/Router";
import "./css/PayPage.css";
import Axios from "axios";

const PayPage = () => {
  const { onClickStart, onClickOwner, onClickPay, onClickDone, onClickMain } =
    useNavigation();
    const [HOTNODDLE, setHOTNODDLE] = useState("");
    const onClickPayment = async () => {
      const quantity = Math.random() * 1000;
      const newHOTNODDLE = "NODDLE" + quantity;
      setHOTNODDLE(newHOTNODDLE);
  
      await window.IMP.init("imp41444282");
  
      window.IMP.request_pay(
        {
          pg: "kakaopay",
          pay_method: "card",
          amount: "100",
          name: "매운 라면",
          merchant_uid: newHOTNODDLE,
        },
        async (rsp) => {
          if (rsp.success) {
            // 결제 성공 시 처리
            // 예: 페이지 이동
            window.location.href = '/결제완료';
          } else {
            // 결제 실패 시 처리
            alert("결제에 실패하였습니다: " + rsp.error_msg);
          }
        }
      );
    };
    
  return (
    <div className="wrapper1">
      <header>
        <div className="Header1">
          <div className="goods1">1. 상품확인</div>
          <div className="pay1">2. 결제 방법</div>
          <div className="done1">3. 결제 완료</div>
        </div>
      </header>
      <div className="wrapper">
        <div className="txt">결제 수단을 선택해주세요</div>
        <div className="selectCash">
          <button className="cash"  onClick={onClickPayment}>
            카카오페이
          </button>
          <button className="card" onClick={onClickDone}>
            현금
          </button>
        </div>
      </div>
    </div>
  );
};

export default PayPage;
