import React, { useEffect, useReducer, useCallback, useState } from "react";
import {dispatch} from "./item/Item"
import { useNavigation } from "../Router/Router";
import "./css/PayPage.css"
import Axios from 'axios';
const PayPage = () => {
  const { onClickStart, onClickOwner, onClickPay, onClickDone, onClickMain } =
    useNavigation();
    onClickDone();
    IMP.init("imp41444282");

  const button = document.querySelector("button");
  var quantity = Math.random() * 1000;

const PayFunction = async () => {
    var HOTNODDLE = "NODDLE"+quantity;
    IMP.request_pay({
        pg: "kakaopay",
        pay_method: "card",
        amount: "100",
        name: "매운 라면",
        merchant_uid: HOTNODDLE,
    }, async (rsp) => {
        if (rsp.success) {
            quantity += 1;
            window.location.href = '/결제완료';
        } else {
            // 결제가 실패한 경우에 대한 처리를 여기에 추가할 수 있습니다.
            alert("결제에 실패하였습니다: " + rsp.error_msg);
        }
    });
  }
  return (
    <div className="wrapper1">
          <header>
            <div className="Header1">
              <div className="goods1">1. 상품확인</div>
              <div className="pay1">2. 결제 방법</div>
              <div className="done1">3. 결제 완료</div>
            </div>
          </header>
      <div className="wrapper">
        <div className="txt">결제 수단을 선택해주세요</div>
        <div className="selectCash">
          <button className="cash" onClick={PayFunction}>카카오페이</button>
          <button className="card"  onClick={PayFunction}>현금</button>
        </div>
      </div>
    </div>
  );
};
export default PayPage;
