import React, { useEffect, useReducer, useCallback, useState } from "react";
import { useNavigation } from "../../Router/Router";
import ItemCount from "./ItemCount";
import image1 from "./1.jpg";
import image2 from "./2.jpg";
import Axios from 'axios';
import minus from "./minus.png";
import "./css/Item.css";
const initalState = {
  idx: 3,
  mockData: [
    {
      id: 1,
      img: image1,
      name: "농심 새우깡 500g",
      amount: 1,
      price: 1500,
    },
    {
      id: 2,
      img: image2,
      name: "카레여왕 500g",
      amount: 2,
      price: 3000,
    },
  ],
};

const reducer = (state, action) => {
  switch (action.type) {
    case "INCREASE":
      const newIncreaseData = state.mockData.map((item) =>
        item.id === action.data ? { ...item, amount: item.amount + 1 } : item
      );
      return {
        ...state,
        mockData: newIncreaseData,
      };
    case "DECREASE":
      const newDecreaseData = state.mockData.map((item) =>
        item.id === action.data && item.amount > 0
          ? { ...item, amount: item.amount - 1 }
          : item
      );
      return {
        ...state,
        mockData: newDecreaseData,
      };
    case "CLEAR":
      return {
        ...state,
        mockData: [],
      };
    default:
      return state;
  }
};
const Item = () => {
  const { onClickStart, onClickOwner, onClickPay, onClickDone, onClickMain } =
    useNavigation();
  const [state, dispatch] = useReducer(reducer, initalState);
  const [products, setProducts] = useState([]);
  Axios.defaults.baseURL = 'http://127.0.0.1:5000/';


  useEffect(() => {
    // Flask 서버로부터 JSON 데이터를 가져오는 함수
    const fetchData = async () => {
      try {
        console.log("실행중")
        // 엔드포인트에 대한 상대 경로 사용
        const response = await Axios.get('get_products');
        // 서버에서 받은 데이터의 구조에 따라 수정
        console.log(response.data.length);
  
        // 기존의 상품 배열 복사
        const existingProducts = [...products];
  
        // 새로 받은 상품들을 확인하고 기존 상품과 비교하여 수량을 증가시키거나 새로운 상품으로 추가
        for (let i = 0; i < response.data.length; i++) {
          const newProduct = response.data.items[i];
          const existingProductIndex = existingProducts.findIndex(
            oldProduct => oldProduct.name === newProduct.name
          );
  
          if (existingProductIndex !== -1) {
            // 이미 존재하는 상품이라면 수량을 증가시킴
            existingProducts[existingProductIndex].quantity += 1;
          } else {
            // 새로운 상품이라면 배열에 추가
            existingProducts.push({ ...newProduct, quantity: 1 });
          }
        }
  
        // 변경된 상품 배열을 설정
        setProducts(existingProducts);
  
      } catch (error) {
        console.error('데이터를 가져오는 중 오류 발생:', error);
      }
    };
  
    // 컴포넌트가 마운트될 때 데이터 가져오기
    fetchData();
  }, [/* 여기에 의존성 배열 추가 */]);
  return (
    <div className="itemBack">
      <div className="itemMap">
        {products.map((item,index) => (
          <div className="itemflex" key={index}>
            <div className="itemId">
              <p>{index}</p>
            </div>
            <div className="container">
              <img src={item.img} />
            </div>
            <div className="itemNameContainer">
              <p className="itemName">{item.name}</p>
            </div>
            <div className="itemcounter">
            <div className="divplus">
                <button
                  className="button_plus"
                  onClick={() => dispatch({ type: "INCREASE", data: index })}
                ></button>
              </div>
              <p className="itemAmount">{item.amount}</p>
              <div className="divminus">
                <button
                  className="button_minus"
                  onClick={() => dispatch({ type: "DECREASE", data: index })}
                ></button>
              </div>
            </div>
            <div className="itemPrice"> <p>{item.price}원</p></div>
          </div>
        ))}
      </div>

      <div className="buttonContainer">
        <button
          className="buttonClear"
          onClick={() => dispatch({ type: "CLEAR" })}
        >
          전체취소
        </button>
        <button className="buttonCall" onClick={onClickOwner}>
          관리자 호출
        </button>
      </div>

      <ItemCount mockData={products} />
    </div>
  );
};

export default Item;
