import React, { useEffect } from "react";
import { useNavigation } from "../Router/Router";
import "./css/PayPage.css";
import Axios from "axios";

const PayPage = () => {
  const { onClickStart, onClickOwner, onClickPay, onClickDone, onClickMain } =
    useNavigation();
  useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://code.jquery.com/jquery-1.12.4.min.js";
    script.async = true;
    document.body.appendChild(script);

    script.onload = () => {
      const IMP = window.IMP;
      IMP.init("imp41444282"); // 아임포트에서 발급받은 가맹점 식별코드로 변경해야 합니다.

      const payButton = document.getElementById("payButton");
      payButton.addEventListener("click", () => {
        IMP.request_pay(
          {
            pg: "kakaopay",
            pay_method: "card",
            merchant_uid: "주문번호", // 주문번호로 변경해야 합니다.
            amount: 1000, // 결제할 금액으로 변경해야 합니다.
            name: "상품명", // 상품명으로 변경해야 합니다.
            buyer_email: "구매자 이메일", // 구매자 이메일로 변경해야 합니다.
            buyer_name: "구매자 이름", // 구매자 이름으로 변경해야 합니다.
            buyer_tel: "구매자 전화번호", // 구매자 전화번호로 변경해야 합니다.
            m_redirect_url: "결제 완료 후 리다이렉트할 URL", // 결제 완료 후 리다이렉트할 URL로 변경해야 합니다.
          },
          (rsp) => {
            if (rsp.success) {
              // 결제 성공 시 처리
              alert("결제가 완료되었습니다.");
            } else {
              // 결제 실패 시 처리
              alert("결제에 실패하였습니다: " + rsp.error_msg);
            }
          }
        );
      });
    };
  }, []);

  return (
    <div className="wrapper1">
      <header>
        <div className="Header1">
          <div className="goods1">1. 상품확인</div>
          <div className="pay1">2. 결제 방법</div>
          <div className="done1">3. 결제 완료</div>
        </div>
      </header>
      <div className="wrapper">
        <div className="txt">결제 수단을 선택해주세요</div>
        <div className="selectCash">
          <button className="cash" id="payButton">
            카카오페이
          </button>
          <button className="card" onClick={onClickDone}>
            현금
          </button>
        </div>
      </div>
    </div>
  );
};

export default PayPage;
